  <div class="grid-container">
    <div class="single--card">
      <div class="grid-x grid-padding-x grid-margin-y">
        <div class="single--card__column cell medium-4 large-3 small-order-2 medium-order-1 partner--info">
          @if (get_field('horario_parceiro'))
            <h2>Horário</h2>
            <p>{!! the_field('horario_parceiro') !!}</p>
          @endif
          @if (have_rows('contactos_telefonicos'))
            <h2>Contacto Telefónico</h2>
            <ul>
              @while (have_rows('contactos_telefonicos')) @php the_row() @endphp
                 <li>{!! the_sub_field('telefone') !!}</li>
              @endwhile
            </ul>
          @endif
          @if (have_rows('emails'))
            <h2>Email</h2>
            <ul>
              @while (have_rows('emails')) @php the_row() @endphp
                 <li><a href="mailto:{!! the_sub_field('email') !!}">{!! the_sub_field('email') !!}</a></li>
              @endwhile
            </ul>
          @endif
        </div>
        <div class="single--card__column cell medium-8 large-6 small-order-1 medium-order-2">
          <div class="single--card__content">
            {{the_content()}}
          </div>
        </div>
        <div class="single--card__column cell medium-8 large-3 medium-offset-4 large-offset-0 small-order-3 medium-order-3 partner--info">
          @if (get_field('morada'))
            <h2>Morada</h2>
            <p>{!! the_field('morada') !!}</p>
          @endif

          @if (get_field('google_maps'))
            <a target="_blank" href="{!! the_field('google_maps') !!}" class="btn btn-blue partner--info__directions">Obter Direções</a>
          @endif
        </div>
      </div>
    </div>
  </div>
